mapper.sql:
WHERE 1=1

	AND ${SEARCH_KEY} LIKE '%${SEARCH_VALUE}%'


	AND USE_CODE = #{USE_CODE}


	AND LANGUAGE = #{LANGUAGE}


	AND MARKET_YN = #{MARKET_YN}


	AND REG_DATE >= #{SDATE}


	AND REG_DATE <= #{EDATE}



	AND PLAN_CODE= #{PLAN_CODE}


	AND SC_PLAN_CODE= #{SC_PLAN_CODE}


	AND DATE_TYPE = #{DATE_TYPE}



	AND MB_USE_CODE = #{MB_USE_CODE}


	AND MB_TYPE_CODE = #{MB_TYPE_CODE}


	AND MB_NATION = #{MB_NATION}


	AND MB_LANGUAGE = #{MB_LANGUAGE}


	AND MB_COMPANY= #{MB_COMPANY}



	AND USER_CODE = '03'
	AND USER_STATS_YN = 'Y'


	AND USER_CODE = '03'
	AND USER_STATS_YN = 'N'




	AND CATE_CODE= #{CATE_CODE}

mapper.select:
/* getAdminCount */
SELECT * FROM (
	SELECT COUNT(1)totcnt, 1 rownum
	FROM NetMiner.ADMIN_INFO A
	
) NO

/* getAdminInfo */
SELECT A.*
	, DATE_FORMAT(REG_DATE,'%Y-%m-%d')REG_DATES
	, password(#{pwd}) pwd
FROM NetMiner.ADMIN_INFO A
WHERE 1=1
	AND ADMIN_ID = #{ADMIN_ID}
	AND NO = #{NO}

mapper.update:
/* modifyAdminInfo */
UPDATE NetMiner.ADMIN_INFO
	SET UPDATE_DATE = NOW()
	, DESCRIPTION = #{DESCRIPTION}
	, ADMIN_PWD = password(#{ADMIN_PWD})
	WHERE NO=#{NO}

/* insertAdminInfo */
INSERT INTO NetMiner.ADMIN_INFO(ADMIN_CODE, ADMIN_ID, ADMIN_PWD, DESCRIPTION, REG_DATE)
	SELECT '01' ADMIN_CODE, #{ADMIN_ID} ADMIN_ID, password(#{ADMIN_PWD}) ADMIN_PWD, #{DESCRIPTION} DESCRIPTION, now() REG_DATE

mapper.delete:
/* deleteAdminInfo */
DELETE FROM NetMiner.ADMIN_INFO
	WHERE NO=#{NO}

mapper.select:
/* getMemberCount */ SELECT totcnt, rownum FROM ( SELECT COUNT(1)totcnt, 1 rownum FROM ( SELECT A.* , CASE WHEN EXISTS (SELECT NO FROM SUBSCRIPT AA WHERE AA.USER_ID=A.USER_ID AND AA.PLAN_CODE='01') THEN 'Y' ELSE 'N' END FREE_YN FROM NetMiner.MEMBER_INFO A
) A JOIN NATION_CODE B ON A.NATION = B.NATION_CODE LEFT JOIN ( SELECT A.* FROM ( SELECT MAX(NO) SC_NO, REG_DATE SC_REG_DATE , UUID, USER_ID SC_USER_ID, PLAN_CODE SC_PLAN_CODE , ROW_NUMBER() OVER (PARTITION BY USER_ID ORDER BY PLAN_CODE DESC) AS RNUM FROM NetMiner.SUBSCRIPT
GROUP BY UUID, USER_ID, PLAN_CODE )A WHERE RNUM=1 ) SC ON A.UUID = SC.UUID AND USER_CODE='02'
AND FREE_YN=#{FREE_YN}
) NO

/* getMemberList USER_CODE, 01:관리자, 02:일반, 03:휴면,탈퇴 */ SELECT (${COUNT}-NO.RNUM)+1 ROWCNT , NO.* , B.SC_PLAN_CODE , CASE WHEN B.SC_PLAN_CODE='01' THEN 'FREE' WHEN B.SC_PLAN_CODE='02' THEN 'SMALL' WHEN B.SC_PLAN_CODE='03' THEN 'MEDIUM' WHEN B.SC_PLAN_CODE='04' THEN 'LARGE' END SC_PLAN_CODE_STR , DATE_FORMAT(B.SC_REG_DATE,'%Y-%m-%d')SC_REG_DATE_STR , DATE_FORMAT(B.SC_EXITS_DATE,'%Y-%m-%d')SC_EXITS_DATE_STR FROM ( SELECT A.* , CEIL(RNUM / 10) as page FROM ( SELECT A.* , CASE WHEN TYPE_CODE='01' THEN 'Normal' WHEN TYPE_CODE='99' THEN 'Super' END TYPE_CODE_STR , CASE WHEN USE_CODE='01' THEN 'Academic' WHEN USE_CODE='02' THEN 'Commercial' END USE_CODE_STR , DATE_FORMAT(A.REG_DATE,'%Y-%m-%d')REG_DATES , B.NATION_NAME_KR AS NATION_NAME_KR , CASE WHEN EXISTS (SELECT NO FROM SUBSCRIPT AA WHERE AA.USER_ID=A.USER_ID AND AA.PLAN_CODE='01') THEN 'Y' ELSE 'N' END FREE_YN , ROW_NUMBER() OVER (ORDER BY NO DESC) AS RNUM FROM ( SELECT * FROM NetMiner.MEMBER_INFO A
) A JOIN NATION_CODE B ON A.NATION = B.NATION_CODE LEFT JOIN ( SELECT A.* FROM ( SELECT MAX(NO) SC_NO , UUID, USER_ID SC_USER_ID, PLAN_CODE SC_PLAN_CODE , ROW_NUMBER() OVER (PARTITION BY USER_ID ORDER BY PLAN_CODE DESC) AS RNUM FROM NetMiner.SUBSCRIPT
AND EXITS_DATE >= NOW() GROUP BY UUID, USER_ID, PLAN_CODE )A WHERE RNUM=1 ) SC ON A.UUID = SC.UUID
AND USER_CODE='02' )A
WHERE FREE_YN=#{FREE_YN}
) NO LEFT JOIN ( SELECT A.* FROM ( SELECT UUID, USER_ID SC_USER_ID, PLAN_CODE SC_PLAN_CODE, REG_DATE SC_REG_DATE, MAX(EXITS_DATE) SC_EXITS_DATE , ROW_NUMBER() OVER (PARTITION BY UUID ORDER BY PLAN_CODE DESC) AS RNUM FROM NetMiner.SUBSCRIPT
AND EXITS_DATE >= NOW() GROUP BY UUID, USER_ID, PLAN_CODE )A WHERE RNUM=1 ) B ON NO.UUID =B.UUID

mapper.update:
/* modifyMemberInfo */
UPDATE NetMiner.MEMBER_INFO
	SET UPDATE_DATE = NOW()
	, TYPE_CODE= #{TYPE_CODE}
	, USER_CODE= #{USER_CODE}
	, USE_CODE= #{USE_CODE}
	, USER_ID= #{USER_ID}
	, USER_PWD= PASSWORD(#{USER_PWD})
	, LANGUAGE= #{LANGUAGE}
	, NATION= #{NATION}
	, COMPANY= #{COMPANY}
	, MARKET_YN= #{MARKET_YN}
	, MARKET_DATE = NOW()
	WHERE NO=#{NO}

/* modifyMemberInfo */
UPDATE NetMiner.MEMBER_INFO_DROP
	SET UPDATE_DATE = NOW()
	, TYPE_CODE= #{TYPE_CODE}
	, USER_CODE= #{USER_CODE}
	, USE_CODE= #{USE_CODE}
	, USER_ID= #{USER_ID}
	, USER_PWD= PASSWORD(#{USER_PWD})
	, LANGUAGE= #{LANGUAGE}
	, NATION= #{NATION}
	, COMPANY= #{COMPANY}
	, MARKET_YN= #{MARKET_YN} 
	, MARKET_DATE = NOW()
	WHERE NO=#{NO}

mapper.delete:
/* deleteMemberInfo */
DELETE FROM NetMiner.MEMBER_INFO
	WHERE NO=#{NO}

