mapper.select:
SELECT * FROM (
		SELECT A.*
			, DATE_FORMAT(REG_DATE,'%Y-%m-%d')REG_DATES
		FROM NetMiner.EMAIL_INFO A
WHERE A.NO = #{NO}
	) NO

mapper.update:
UPDATE NetMiner.EMAIL_INFO 
	SET EMAIL_CODE = #{EMAIL_CODE}
	, TITLE = #{TITLE} 
	, COMMENT = #{COMMENT} 
WHERE `NO` = ${NO}

UPDATE NetMiner.SUBSCRIPT 
	SET UPDATE_DATE = NOW()
	, PLAN_CODE = #{PLAN_CODE}
	, DATE_TYPE = #{DATE_TYPE}
	, EXITS_DATE = '${EXITS_DATE} 23:59:59'
	, PAY_PLATFORM = '${PAY_PLATFORM}'
	, PAY_PRICE = '${PAY_PRICE}'
WHERE `NO` = ${NO}

mapper.select:
